import React from 'react';
import { MapContainer, TileLayer, useMap, CircleMarker, Popup } from 'react-leaflet'
import ScatterPlot from './Plot.js';
import streetGeometries from './street_locations.json';
import chartData from './chart_data.json';
import './App.css';
import 'leaflet/dist/leaflet.css';

class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      selectedSite: 1,
      hoveredNode: null,
    };

    this.handleChange = this.handleChange.bind(this);
  }

  handleChange(ID) {
    this.setState({selectedSite: ID});
  }

  render() {
    return (
      <div className="App">
        <div>
          <MapContainer id="map-pane" center={[-33.87319087719761, 151.20681949733816]} zoom={12}>
          <TileLayer
            attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
            url="https://{s}.basemaps.cartocdn.com/light_all/{z}/{x}/{y}{r}.png"
          />
          {
            streetGeometries.map((d) => {
              return (
                <CircleMarker
                  center={[d.latitude, d.longitude]}
                  radius={6} fillColor="#ff7800" fillOpacity={0.8} weight={0}
                  eventHandlers={{
                    click: () => this.setState({selectedSite: d.street_id}),
                  }}
                  key={d.street_id}>
                  <Popup>Site ID: {d.street_id}</Popup>
                </CircleMarker>
              )
            })
          }
          </MapContainer>
        </div>
        <div id="visualisation-pane">
          <div id="visualisation-pane-content">
            // <SiteSelector value={this.state.selectedSite} onChange={(ID) => this.handleChange(ID)}/>
            <ScatterPlot data={chartData[this.state.selectedSite]} />
          </div>
        </div>
      </div>
    );
  }
}

