import React from 'react';
import chartData from './chart_data.json';
import './App.css';
import '../node_modules/react-vis/dist/style.css';
import {XYPlot, VerticalGridLines, HorizontalGridLines, XAxis, YAxis, MarkSeries} from 'react-vis';

class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      selectedSite: 1,
      hoveredNode: null,
    };

    this.handleChange = this.handleChange.bind(this);
  }

  handleChange(ID) {
    this.setState({selectedSite: ID});
  }

  render() {
    return (
      <div className="App">
        My own text here instead.
        <div>
          <SiteSelector value={this.state.selectedSite} onChange={(ID) => this.handleChange(ID)}/>
          <DataDisplayer value={this.state.selectedSite} />
          <div className="scatter-plot-chart">
            <XYPlot
              height={500} width={500}
              getX={d => d.temperature_apparent} getY={d => d.pph}
              onMouseLeave={() => this.setState({hoveredNode: null})}>
              <VerticalGridLines />
              <HorizontalGridLines />
              <XAxis />
              <YAxis />
              <MarkSeries
                data={chartData[this.state.selectedSite]}
                onValueMouseOver={d => this.setState({hoveredNode: d})}
                />
            </XYPlot>
          </div>
        </div>
      </div>
    );
  }
}

function SiteSelector(props) {
  const options = Object.keys(chartData).map((k) => {
    return (
      <option key={k} value={k}>{k}</option>
    )
  });
  return (
    <form>
      <label>
        Select your site:
        <select value={props.value} onChange={(e) => props.onChange(e.target.value)}>
          {options}
        </select>
      </label>
    </form>
  );
}

function DataDisplayer(props) {
  return (
    <div>
      The time of the first observation at the selected site is: {chartData[props.value][0]["time"]}.
    </div>
  );
}

export default App;
