import React from 'react';
import { MapContainer, TileLayer, useMap, CircleMarker, Popup } from 'react-leaflet'
import ScatterPlot from './Plot.js';
import streetGeometries from './street_locations.json';
import chartData from './chart_data.json';
import './App.css';
import 'leaflet/dist/leaflet.css';

const sorted = (l, descending) => {
  let c = [...l];
  c.sort();
  if (!descending) {
    c.reverse()
  }
  return c;
}

class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      selectedSite: 1,
      hoveredNode: null,
    };
    this.handleChange = this.handleChange.bind(this);
  }

  handleChange(ID) {
    this.setState({selectedSite: ID});
  }

  render() {
    return (
      <div className="App">
        <SiteLocationsMap circleOnClick={(ID) => this.handleChange(ID)} />
        <div id="visualisation-pane">
          <div id="visualisation-pane-content">
            {/* <SiteSelector value={this.state.selectedSite} onChange={(ID) => this.handleChange(ID)}/> */}
            <ScatterPlot data={chartData[this.state.selectedSite]} />
            <p>Site ID: {this.state.selectedSite}.</p>
            <p><i>Example Street between Cross Street and Side Street.</i></p>
            <p>
              Between {Math.min(...chartData[this.state.selectedSite].map((d) => d.pph))} and {Math.max(...chartData[this.state.selectedSite].map((d) => d.pph))} people
              per hour have been surveyed at this site over <i>n</i> unique dates,
              including <i>n</i> unique days of the week, between the hours
              of {sorted(chartData[this.state.selectedSite].map((d) => d.time))[0].substring(0, 2)}:00
              and {(sorted(chartData[this.state.selectedSite].map((d) => d.time)), true)[0].substring(0, 2)}:00.
            </p>
            <p>
              The entire dataset, including disaggregations by footpath and direction, is available to
              download <a href="https://public-life-observations.s3.ap-southeast-2.amazonaws.com/parquet/movements.parquet">here</a> as
              open data under
              a <a href="https://creativecommons.org/licenses/by/4.0/legalcode">Creative Commons Attribution 4.0</a> licence.
            </p>
          </div>
        </div>
      </div>
    );
  }
}

function SiteLocationsMap(props) {
  return (
    <div>
      <MapContainer id="map-pane" center={[-33.87319087719761, 151.20681949733816]} zoom={13} zoomControl={false}>
      <TileLayer
        attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OSM</a> contributors, <a href="https://carto.com/attributions">CARTO</a>'
        url="https://{s}.basemaps.cartocdn.com/light_all/{z}/{x}/{y}{r}.png"
      />
      {
        streetGeometries.map((d) => {
          return (
            <CircleMarker
              center={[d.latitude, d.longitude]}
              radius={6} fillColor="#ff7800" fillOpacity={0.8} weight={0}
              eventHandlers={{
                click: () => props.circleOnClick(d.street_id),
              }}
              key={d.street_id}>
              <Popup>Site ID: {d.street_id}</Popup>
            </CircleMarker>
          )
        })
      }
      </MapContainer>
    </div>
  );
}

