import React from 'react';
import { MapContainer, TileLayer, useMap, CircleMarker, Popup } from 'react-leaflet'
import ScatterPlot from './Plot.js';
import chartData from './chart_data.json';
import './App.css';
import 'leaflet/dist/leaflet.css';

class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      selectedSite: 1,
      hoveredNode: null,
    };

    this.handleChange = this.handleChange.bind(this);
  }

  handleChange(ID) {
    this.setState({selectedSite: ID});
  }

  render() {
    return (
      <div className="App">
        <div>
        <MapContainer id="map-pane" center={[-33.87319087719761, 151.20681949733816]} zoom={12}>
        <TileLayer
          attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
          url="https://{s}.basemaps.cartocdn.com/light_all/{z}/{x}/{y}{r}.png"
        />
        <CircleMarker center={[-33.871011241728354, 151.2224784032214]} pathOptions={{ color: 'red' }} radius={20}>
          <Popup>Popup in CircleMarker</Popup>
        </CircleMarker>
        </MapContainer>
        </div>
        <div id="visualisation-pane">
          <div id="visualisation-pane-content">
            <SiteSelector value={this.state.selectedSite} onChange={(ID) => this.handleChange(ID)}/>
            <ScatterPlot data={chartData[this.state.selectedSite]} />
          </div>
        </div>
      </div>
    );
  }
}

function SiteSelector(props) {
  const options = Object.keys(chartData).map((k) => {
    return (
      <option key={k} value={k}>{k}</option>
    )
  });
  return (
    <form>
      <label>
        Select your site:
        <select value={props.value} onChange={(e) => props.onChange(e.target.value)}>
          {options}
        </select>
      </label>
    </form>
  );
}

export default App;
