import React from 'react';
import { MapContainer, TileLayer, useMap, Marker, Popup } from 'react-leaflet'
import ScatterPlot from './Plot.js';
import chartData from './chart_data.json';
import './App.css';
import 'leaflet/dist/leaflet.css';

class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      selectedSite: 1,
      hoveredNode: null,
    };

    this.handleChange = this.handleChange.bind(this);
  }

  handleChange(ID) {
    this.setState({selectedSite: ID});
  }

  render() {
    return (
      <div className="App">
        <div id="map-pane">
        <MapContainer style={{ height: "450px", width: "100%" }} center={[51.505, -0.09]} zoom={13} scrollWheelZoom={false}>
        <TileLayer
          attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
          url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
        />
        <Marker position={[51.505, -0.09]}>
          <Popup>
            A pretty CSS3 popup. <br /> Easily customizable.
          </Popup>
        </Marker>
        </MapContainer>
        </div>
        <div id="visualisation-pane">
          <div id="visualisation-pane-content">
            <SiteSelector value={this.state.selectedSite} onChange={(ID) => this.handleChange(ID)}/>
            <ScatterPlot data={chartData[this.state.selectedSite]} />
          </div>
        </div>
      </div>
    );
  }
}

function SiteSelector(props) {
  const options = Object.keys(chartData).map((k) => {
    return (
      <option key={k} value={k}>{k}</option>
    )
  });
  return (
    <form>
      <label>
        Select your site:
        <select value={props.value} onChange={(e) => props.onChange(e.target.value)}>
          {options}
        </select>
      </label>
    </form>
  );
}

export default App;
