import React from 'react';
import { Group } from '@visx/group';
import { Circle } from '@visx/shape';
import { scaleLinear, scaleTime, scaleQuantize } from '@visx/scale';
import { AxisLeft, AxisBottom } from '@visx/axis';
import { GridRows, GridColumns } from '@visx/grid';
import { schemeRdYlBu, interpolateRdYlBu } from 'd3-scale-chromatic';

// const data = [
//   {"street_group_id": 12, "time": "17:27:46", "pph": 1152.0, "temperature_apparent": 11.6},
//   {"street_group_id": 12, "time": "17:33:36", "pph": 1188.0, "temperature_apparent": 10.6},
//   {"street_group_id": 12, "time": "18:14:16", "pph": 1260.0, "temperature_apparent": 10.4},
//   {"street_group_id": 12, "time": "18:19:58", "pph": 1884.0, "temperature_apparent": 10.4},
//   {"street_group_id": 12, "time": "13:35:34", "pph": 1008.0, "temperature_apparent": 12.2},
//   {"street_group_id": 12, "time": "14:03:34", "pph": 1236.0, "temperature_apparent": 11.3},
//   {"street_group_id": 12, "time": "14:09:14", "pph": 2100.0, "temperature_apparent": 11.3},
//   {"street_group_id": 12, "time": "16:47:24", "pph": 1056.0, "temperature_apparent": 11.9},
//   {"street_group_id": 12, "time": "16:53:03", "pph": 1152.0, "temperature_apparent": 11.9},
//   {"street_group_id": 12, "time": "13:29:51", "pph": 1956.0, "temperature_apparent": 12.5}
// ];

// Define the graph dimensions and margins
const width = 500;
const height = 500;
const margin = { top: 40, bottom: 50, left: 40, right: 30 };

// Then we'll create some bounds
const xMax = width - margin.left - margin.right;
const yMax = height - margin.top - margin.bottom;

// We'll make some helpers to get at the data we want
const x = d => Date.parse("2022-09-11T" + d.time);
const y = d => d.pph;
const temperatureAccessor = d => d.temperature_apparent;

// And then scale the graph by our data
const xScale = scaleTime({
  range: [0, xMax],
  round: true,
  domain: [Date.parse("2022-09-11T06:00:00"), Date.parse("2022-09-11T23:00:00")], // Math.max(...data.map(x))
  padding: 0.4,
});
const yScale = scaleLinear({
  range: [yMax, 0],
  round: true,
  domain: [0, 2500], // Math.max(...data.map(y))] // change this to an API fetch for max PPH
});
const temperatureRescaler = scaleLinear({
  range: [0, 1],
  domain: [Math.min(...data.map(temperatureAccessor)), Math.max(...data.map(temperatureAccessor))],
})

// Compose together the scale and accessor functions to get point functions
const compose = (scale, accessor) => data => scale(accessor(data));
const xPoint = compose(xScale, x);
const yPoint = compose(yScale, y);
const getTemperatureStandardised = compose(temperatureRescaler, temperatureAccessor);
const temperatureColourScale = (d) => interpolateRdYlBu(getTemperatureStandardised(d))

// Finally we'll embed it all in an SVG
function ScatterPlot(props) {
  return (
    <div>
      <svg width={width} height={height} className="scatter-plot-chart">
        {this.props.data.map((d, i) => {
          return (
            <Group key={`bar-${i}`}>
              <GridRows scale={yScale} width={xMax} height={yMax} stroke="#e0e0e0" />
              <GridColumns scale={xScale} width={xMax} height={yMax} stroke="#e0e0e0" />
              <AxisBottom top={yMax} scale={xScale} numTicks={width > 520 ? 10 : 5} />
              <AxisLeft scale={yScale} />
              <Circle
                cx={xPoint(d)}
                cy={yPoint(d)} // {yMax - barHeight}
                r={10}
                fill={temperatureColourScale(d)}
              />
            </Group>
          );
        })}
      </svg>
    </div>
  );
}

export default ScatterPlot;
