import React from 'react';
import chartData from './chart_data.js';
import './App.css';

class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {selectedSite: 1};

    this.handleChange = this.handleChange.bind(this);
  }

  handleChange(ID) {
    this.setState({selectedSite: ID});
  }

  render() {
    return (
      <div className="App">
        My own text here instead.
        <div>
          <SiteSelector value={this.state.selectedSite} onChange={(ID) => this.handleChange(ID)}/>
          <DataDisplayer value={this.state.selectedSite} />
        </div>
      </div>
    );
  }
}

function SiteSelector(props) {
  const options = chartData.keys().map((k) => {
    return (
      <option key={k} value={k}>{k}</option>
    )
  });
  return (
    <form>
      <label>
        Select your site:
        <select value={props.value} onChange={(e) => props.onChange(e.target.value)}>
          {options}
        </select>
      </label>
    </form>
  );
}

function DataDisplayer(props) {
  return (
    <div>
      The selected site is: {chartData[props.value][0]["time"]}.
    </div>
  );
}

export default App;
