import React from 'react';
// import chartData from './chart_data.json';
import './App.css';

class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {selectedSite: 1};

    this.handleChange = this.handleChange.bind(this);
  }

  handleChange(event) {
    this.setState({selectedSite: event.target.value});
  }

  render() {
    return (
      <div className="App">
        My own text here instead.
        <div>
          <SiteSelector value={this.state.selectedSite} onChange={(e) => this.handleChange(e)}/>
          <DataDisplayer value={this.state.selectedSite} />
        </div>
      </div>
    );
  }
}

function SiteSelector(props) {
  return (
    <form>
      <label>
        Select your site:
        <select value={props.value} onChange={props.onChange()}>
          <option value="1">1</option>
          <option value="2">2</option>
          <option value="3">3</option>
          <option value="4">4</option>
        </select>
      </label>
    </form>
  );
}

function DataDisplayer(props) {
  return (
    <div>
      The selected site is: {props.value}.
    </div>
  );
}

export default App;
